const $ = document.querySelector.bind(document);
const $$ = document.querySelectorAll.bind(document);

const PLAYER_STORAGE_KEY = 'F8_PLAYER';

const player = $('.player');
const cd = $('.cd');
const heading = $('header h2');
const cdThumb = $('.cd-thumb');
const audio = $('#audio');
const playBtn = $('.btn-toggle-play');
const progress = $('#progress');
const prevBtn = $('.btn-prev');
const nextBtn = $('.btn-next');
const randomBtn = $('.btn-random');
const repeatBtn = $('.btn-repeat');
const playlist = $('.playlist');

const app = {
    currentIndex: 0,
    isPlaying: false,
    isRandom: false,
    isRepeat: false,
    config: JSON.parse(localStorage.getItem(PLAYER_STORAGE_KEY)) || {},
    songs: [
        {
            name: "Nắm đôi bàn tay",
            singer: "KayTran",
            path: "https://f9-stream.nixcdn.com/NhacCuaTui1017/NamDoiBanTay-KayTran-7042104.mp3?st=2qS5hgxToo9DPOX0t0jBSw&e=1626158533&download=true",
            image: "https://file.tinnhac.com/resize/600x-/2021/07/03/20210703102751-40cf.jpg"
        },
        {
            name: "Nàng Thơ",
            singer: "Hoàng Dũng",
            path: "https://c1-ex-swe.nixcdn.com/NhacCuaTui1001/NangTho-HoangDung-6413381.mp3?st=LBNmzaHnWwloM9X8peZRpg&e=1626158955&download=true",
            image: "https://yt3.ggpht.com/ytc/AKedOLQhsN2L1n4rJ1dShtnORNtRVFHecnw-teIurv0Xow=s900-c-k-c0x00ffffff-no-rj"
        },
        {
            name: "Níu Duyên",
            singer: "Lê Bảo Bình",
            path:
                "https://c1-ex-swe.nixcdn.com/NhacCuaTui1008/NiuDuyen-LeBaoBinh-6872127.mp3?st=cFm801vOnCrQdYuTntjKZw&e=1626159144&download=true",
            image: "data:image/jpeg;base64,"
        },
        {
            name: "Sài Gòn Đau Lòng Quá",
            singer: "Hoàng Duyên",
            path: "https://c1-ex-swe.nixcdn.com/NhacCuaTui1013/SaiGonDauLongQua-HuaKimTuyenHoangDuyen-6992977.mp3?st=G0kOBrkgcv-MIG6x1b91Ug&e=1626159319&download=true",
            image:
                "data:image/jpeg;base64,"
        },
        {
            name: "Trắc Trở",
            singer: "X2X",
            path: "https://f9-stream.nixcdn.com/NhacCuaTui1017/TracTro-X2X-7040184.mp3?st=v3_TybfxrUmuPmlhgAFBiA&e=1626159787&download=true",
            image:
                "https://i.ytimg.com/vi/SrTcr-6QBHA/maxresdefault.jpg"
        },
        {
            name: "Hiện Đại",
            singer: "Khắc Việt",
            path:
                "https://c1-ex-swe.nixcdn.com/NhacCuaTui1016/HienDai-KhacViet-7022864.mp3?st=mqzhME3jC8ZNtdXYzYV0LA&e=1626160207&download=true",
            image:
                "data:image/jpeg;base64,"
        },
        {
            name: "Có Không Giữ Mất Đừng Tìm",
            singer: "Cảnh Minh",
            path: "https://c1-ex-swe.nixcdn.com/NhacCuaTui926/CoKhongGiuMatDungTim-CanhMinh-3847487.mp3?st=CpHAWL-0FD1TY6nFPIYxSg&e=1626160314&download=true",
            image:
                "data:image/jpeg;base64,"
        }
    ],
     setConfig: function(key, value) {
         this.config[key] = value;
         localStorage.setItem(PLAYER_STORAGE_KEY, JSON.stringify(this.config))

     },
    // render ra danh sach bai hat
    render: function () {
        const htmls = this.songs.map((song, index) => {
            return `
            <div class="song ${index === this.currentIndex ? 'active' : ''} "data-index = "${index}">
                <div class="thumb"
                    style="background-image: url('${song.image}')">
                </div>
                <div class="body">
                    <h3 class="title">${song.name}</h3>
                    <p class="author">${song.singer}</p>
                </div>
                <div class="option">
                    <i class="fas fa-ellipsis-h"></i>
                </div>
            </div>
            `
        })
        playlist.innerHTML = htmls.join('')

    },

    defineProperties: function () {
        Object.defineProperty(this, 'currentSong', {
            get: function () {
                return this.songs[this.currentIndex]
            }
        })
    },

    handleEvents: function () {
        const _this = this;
        const cdWidth = cd.offsetWidth;

        // Xử lý Cd quay và dừng
        const cdThumbAnimate = cdThumb.animate([
            { transform: 'rotate(360deg)' }
        ], {
            duration: 10000,//10 giay
            iterations: Infinity
        })
        cdThumbAnimate.pause();

        // cuộn hình ảnh bài hát đang phát thu nhỏ phóng to
        document.onscroll = function () {
            const srcollTop = window.scrollY;
            const newCdWidth = cdWidth - srcollTop;
            cd.style.width = newCdWidth > 0 ? newCdWidth + 'px' : 0;
            cd.style.opacity = newCdWidth / cdWidth;
        }

        // xu ly khi click play
        playBtn.onclick = function () {
            if (_this.isPlaying) {
                audio.pause();
            } else {
                audio.play();
            }

        }
        // khi song được play
        audio.onplay = function () {
            _this.isPlaying = true;
            player.classList.add('playing');
            cdThumbAnimate.play();

        }
        // khi pause được pause
        audio.onpause = function () {
            _this.isPlaying = false;
            player.classList.remove('playing')
            cdThumbAnimate.pause();

        }

        // Khi tiến độ bài hát thay đổi
        audio.ontimeupdate = function () {
            if (audio.duration) {
                const progressPercent = Math.floor(audio.currentTime / audio.duration * 100);
                progress.value = progressPercent
            }
        }

        // Xử lý khi tua song
        progress.oninput = function (e) {
            const seekTime = audio.duration / 100 * e.target.value;
            audio.currentTime = seekTime;
        }

        // khi next song
        nextBtn.onclick = function () {
            if (_this.isRandom) {
                _this.playRandomSong();

            } else {
                _this.nextSong();
            }
            audio.play();
            _this.render();
            _this.scrollToActiveSong();
        }

        // khi pause song
        prevBtn.onclick = function () {
            if (_this.isRandom) {
                _this.playRandomSong();

            } else {
                _this.prevSong();
            }
            audio.play();
            _this.render();
            _this.scrollToActiveSong();
        }

        // XU ly random bat/tat random song
        randomBtn.onclick = function (e) {
            _this.isRandom = !_this.isRandom;
            _this.setConfig('isRandom', _this.isRandom);
            randomBtn.classList.toggle('active', _this.isRandom);

        }

        // Xu ly lap lai bai hat 
        repeatBtn.onclick = function (e) {
            _this.isRepeat = !_this.isRepeat;
            _this.setConfig('isRepeat', _this.isRepeat);
            repeatBtn.classList.toggle('active', _this.isRepeat);
        }

        //xu ly next song khi audio 
        audio.onended = function () {
            if (_this.isRepeat) {
                audio.play();
            } else {
                nextBtn.click();
            }
        }

        // lang nghe hành vi click vào playlist
        playlist.onclick = function (e) {
            const songNode = e.target.closest('.song:not(.active)');
            if (songNode || e.target.closest('.option')) {
                //xu ly khi click vao song
                if (songNode) {
                    _this.currentIndex = Number(songNode.dataset.index);
                    _this.loadCurrentSong();
                    _this.render();
                    audio.play();
                }

                // xu ly khi click vao option
                if (e.target.closest('.option')) {

                }
            }

        }


    },

    scrollToActiveSong: function () {
        setTimeout(() => {
            $('.song.active').scrollIntoView({
                behavior: 'smooth',
                block: 'center',
            })

        }, 300)

    },

    loadCurrentSong: function () {
        heading.textContent = this.currentSong.name;
        cdThumb.style.backgroundImage = `url(${this.currentSong.image})`;
        audio.src = this.currentSong.path;

    },
    loadConfig: function(){
        this.isRandom = this.config.isRandom;
        this.isRepeat = this.config.isRepeat;

    },

    nextSong: function () {
        this.currentIndex++
        if (this.currentIndex >= this.songs.length) {
            this.currentIndex = 0;

        }
        this.loadCurrentSong();
    },

    prevSong: function () {
        this.currentIndex--
        if (this.currentIndex < 0) {
            this.currentIndex = this.songs.length - 1;

        }
        this.loadCurrentSong();
    },

    playRandomSong: function () {
        let newIndex
        do {
            newIndex = Math.floor(Math.random() * this.songs.length)
        } while (newIndex === this.currentIndex)
        this.currentIndex = newIndex;
        this.loadCurrentSong();

    },

    start: function () {
        //Gan cau hinh tu config vao ung dung
        this.loadConfig();

        //Định nghĩa các thuộc tính cho object
        this.defineProperties();

        //lắng nghe / xưt lý các sự kiện
        this.handleEvents();

        //tải thông tin bài đầu tiên 
        this.loadCurrentSong();

        this.render();


        // hien thi trang thai ban dau cua button repeat va random 
        randomBtn.classList.toggle('active', this.isRandom);
        repeatBtn.classList.toggle('active', this.isRepeat);

    }
};

app.start();
